import { useState } from 'react'
import './App.css';
import Create from './Component/Create'
import List from './Component/List'
import Navbar from './Component/Navbar'
import Search from './Component/Search'
import { v4 as id } from 'uuid'
function App() {
  const [search, setSearch] = useState('')
  const [fill, setFill] = useState('all')
  console.log(fill);

  const [ movies , setMovies] = useState([
    { name: "Avatar" , rating: 980, id: id() , like: false},
    { name: "Titanik", rating: 890, id: id(), like: true },
    { name: "King Kong", rating: 985, id: id(), like:  false },
    { name: "Harry Potter", rating: 881, id: id(), like: true },
    { name: "Stranger things", rating: 999, id: id(), like: false }
  ])
  function LenghtLike( like ){
      return movies.filter((item) => item.like == like)
  }
  const Count = LenghtLike(true)

  // Filters
  const handleFilter = (arr, key) => {
    if (key === 'all') return arr
    else if(key==='famous') return arr.filter((item)=>item.like)
    else if (key === 'rating') return arr.filter((item) => item.rating >900)
  }


  // ADD MOVIE
  function addMovie(newMovie){
    setMovies((prev) => {
      return [...prev, newMovie]
    })
    console.log(movies);
  }
  // Search
  function SearchHandler( arr , key){
    if ( key.length == 0){
      return arr  
    }
    else{
      return arr.filter((item) => item.name.toLowerCase().includes(key.toLowerCase()))
    }
  }
  const filterMovies = handleFilter(SearchHandler(movies, search),fill)
  console.log(filterMovies);

  return (
    <div className="App">
      <div className="all">
        <Navbar length={movies.length} LenghtLike={Count}/>
        <Search setSearch={setSearch} setFill={setFill}/>
        <List movies={filterMovies} setMovies={setMovies}/>
        <Create addMovie={addMovie}/>
      </div>
    </div>
  );
}

export default App;
