import { useState } from 'react'
import './App.css';
import Create from './Component/Create'
import List from './Component/List'
import Navbar from './Component/Navbar'
import Search from './Component/Search'
import { v4 as id } from 'uuid'
function App() {
  const [ movies , setMovies] = useState([
    {name: "Avatar" , rating: 980 ,  id: id() , like: true},
    { name: "Titanik", rating: 990, id: id(), like: true },
    { name: "King Kong", rating: 985, id: id(), like:  false },
    { name: "Harry Potter", rating: 981, id: id(), like: false },
    { name: "Stranger things", rating: 999, id: id(), like: false }
  ])
  // ADD MOVIE
  function addMovie(newMovie){
    setMovies((prev) => {
      return [...prev, newMovie]
    })
    console.log(movies);

  }
  return (
    <div className="App">
      <div className="all">
        <Navbar />
        <Search />
        <List movies={movies} />
        <Create addMovie={addMovie}/>
      </div>
    </div>
  );
}

export default App;
