import React from 'react'

function List({ movies, setMovies}) {
  function toggleStar(id){
    return movies.map((movie) => movie.id === id ? { ...movie , like: !movie.like } : movie)
  }
  return (
	<div className='List'>
    {movies.map((movie) => {
      return(
        <div className="div" key={movie.id}>
          <div className="div_in">
            <div className="name">
              <p>{movie.name}</p>
            </div>
            <div className="info">
              <p>{movie.rating}</p>
              <i className="fa-regular fa-eye" onClick={toggleStar(movie.id)}></i>
              <i className="fa-solid fa-trash" onClick={() => setMovies(movies.filter((m) => m.id !== movie.id))}></i>
              <i className="fa-solid fa-star" style={{opacity: movie.like ? 1 : 0, color: 'gold'}}></i>
            </div>
          </div>
          <hr />
        </div>
      )
    })}
  </div>
  )
}

export default List
