import React from 'react'
import { useState } from 'react'

function Search({ setSearch, setFilter }) {
	const [ style , setStyle ] = useState('all')
	return (
		<div className='Search'>
			<input type="text" placeholder='Kinolarni qidirish' onChange={(e) => { setSearch (e.target.value)}}/>
			<button onClick={() =>setFilter('all')} className={setFilter === 'all' ? 'active' : null }>Barcha kinolar</button>
			<button onClick={() =>setFilter('famous')} className={setFilter === 'famous' ? 'active' : null}>Mashhur kinolar</button>
			<button onClick={() =>setFilter('rating')} className={setFilter === ''}>Eng ko'p ko'rilgan kinolar</button>
		</div>
	)
}

export default Search
