import React from 'react'
import { useState } from 'react'

function Search({ setSearch, setFilter }) {
	function color(e){
		e.style.target.bacgroundColor = 'black'
		e.style.target.color = 'white'
	}
	return (
		<div className='Search'>
			<input type="text" placeholder='Kinolarni qidirish' onChange={(e) => { setSearch (e.target.value)}}/>
			<button onClick={(e) => color(e.tage)}>Barcha kinolar</button>
			<button onClick={() =>setFilter('famous')}>Mashhur kinolar</button>
			<button onClick={() =>setFilter('rating')}>Eng ko'p ko'rilgan kinolar</button>
		</div>
	)
}

export default Search
